         {id: 2, text: 'Salario', monto: 300},
         {id: 3, texto: 'Libro', monto: -10},
         {id: 4, texto: 'Cámara', cantidad: 150}
        ]
}


// Create Contest
export const globalContext = createContext(initialState);

//Provider Component
export const globalProvider = ({children}) => {
